import React, { useState, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import "./index.css";

const App = () => {
  const [formData, setFormData] = useState({
    budget: "Affordable",
    customBudget: "",
    currency: "USD",
    duration: "",
    address: "",
    destination: "",
    purpose: "Leisure",
    customPurpose: "",
    preferences: "",
  });
  const [itinerary, setItinerary] = useState("");
  const [loading, setLoading] = useState(false);
  const [abortController, setAbortController] = useState(null);

  useEffect(() => {
    if (itinerary) {
      window.scrollTo({ top: 0, behavior: "smooth" });
    }
  }, [itinerary]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setItinerary("");

    const controller = new AbortController();
    setAbortController(controller);

    try {
      const budgetValue =
        formData.budget === "Custom"
          ? `${formData.customBudget} ${formData.currency}`
          : formData.budget;
      const purposeValue =
        formData.purpose === "Other" ? formData.customPurpose : formData.purpose;

      const requestBody = {
        budget: budgetValue,
        duration: formData.duration,
        destination: formData.destination,
        purpose: purposeValue,
        preferences: formData.preferences,
        ...(formData.address && { address: formData.address }),
      };

      const response = await fetch("http://localhost:5000/generate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(requestBody),
        signal: controller.signal, // Attach the abort signal here
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      if (!response.body) {
        throw new Error("ReadableStream not supported in this browser.");
      }

      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let result = "";

      while (true) {
        const { value, done } = await reader.read();
        if (done) break;
        result += decoder.decode(value, { stream: true });
        setItinerary(result); // Update itinerary text as it streams in.
      }
    } catch (error) {
      if (error.name === "AbortError") {
        setItinerary("Itinerary generation stopped.");
      } else {
        console.error("Fetch error:", error);
        setItinerary("Failed to connect to server. Please try again later.");
      }
    }

    setLoading(false);
    setAbortController(null);
  };

  const handleStop = () => {
    if (abortController) {
      abortController.abort(); // Stop the fetch request
      setAbortController(null);
      setLoading(false);
    }
  };

  return (
    <div className="container">
      {loading && <div className="loading-overlay">Generating your itinerary...</div>}

      <h1>Travel Itinerary Generator</h1>
      <form onSubmit={handleSubmit}>
        {/* Existing form fields */}
        <button type="submit" className="submit-btn" disabled={loading}>
          {loading ? "Generating..." : "Generate Itinerary"}
        </button>
        {loading && (
          <button type="button" className="stop-btn" onClick={handleStop}>
            Stop
          </button>
        )}
      </form>

      {itinerary && (
        <div className="itinerary markdown-output">
          <h2>Your Itinerary</h2>
          <ReactMarkdown>{itinerary}</ReactMarkdown>
        </div>
      )}
    </div>
  );
};

export default App;
