import React, { useState, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import "./index.css";

const App = () => {
  const [formData, setFormData] = useState({
    budget: "Affordable",
    customBudget: "",
    currency: "USD",
    duration: "",
    address: "",
    destination: "",
    purpose: "Leisure",
    customPurpose: "",
    preferences: "",
  });
  const [itinerary, setItinerary] = useState("");
  const [loading, setLoading] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [userInput, setUserInput] = useState("");

  useEffect(() => {
    if (itinerary) {
      window.scrollTo({ top: 0, behavior: "smooth" });
    }
  }, [itinerary]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setItinerary("");
    try {
      const budgetValue =
        formData.budget === "Custom"
          ? `${formData.customBudget} ${formData.currency}`
          : formData.budget;
      const purposeValue =
        formData.purpose === "Other" ? formData.customPurpose : formData.purpose;

      const response = await fetch("http://localhost:5000/generate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          budget: budgetValue,
          duration: formData.duration,
          address: formData.address,
          destination: formData.destination,
          purpose: purposeValue,
          preferences: formData.preferences,
        }),
      });

      if (!response.body) throw new Error("No response body");
      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let result = "";

      while (true) {
        const { value, done } = await reader.read();
        if (done) break;
        result += decoder.decode(value, { stream: true });
        setItinerary(result);
      }
    } catch (error) {
      setItinerary("Failed to connect to server.");
    }
    setLoading(false);
  };

  const handleChatSubmit = async (e) => {
    e.preventDefault();
    if (!userInput.trim()) return;

    const newChatMessages = [...chatMessages, { sender: "You", text: userInput }];
    setChatMessages(newChatMessages);
    setUserInput("");

    try {
      const response = await fetch("http://localhost:5000/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: userInput }),
      });

      if (!response.body) throw new Error("No response body");
      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let result = "";

      while (true) {
        const { value, done } = await reader.read();
        if (done) break;
        result += decoder.decode(value, { stream: true });
        setChatMessages([...newChatMessages, { sender: "AI", text: result }]);
      }
    } catch (error) {
      setChatMessages([...newChatMessages, { sender: "AI", text: "Error fetching response." }]);
    }
  };

  return (
    <div className="container">
      {loading && <div className="overlay"><div className="spinner"></div></div>}

      <h1>Travel Itinerary Generator</h1>
      <form onSubmit={handleSubmit}>
        <label>Budget:</label>
        <select name="budget" value={formData.budget} onChange={handleChange}>
          <option value="Luxury">Luxury</option>
          <option value="Premium">Premium</option>
          <option value="Affordable">Affordable</option>
          <option value="Backpacker">Backpacker</option>
          <option value="Custom">Custom</option>
        </select>
        {formData.budget === "Custom" && (
          <div className="inline-fields">
            <input
              type="number"
              name="customBudget"
              value={formData.customBudget}
              onChange={handleChange}
              required
              placeholder="e.g., 500"
            />
            <select name="currency" value={formData.currency} onChange={handleChange}>
              <option value="USD">USD</option>
              <option value="EUR">EUR</option>
              <option value="INR">INR</option>
              <option value="GBP">GBP</option>
            </select>
          </div>
        )}
        <button type="submit" className="submit-btn" disabled={loading}>
          {loading ? "Generating..." : "Generate Itinerary"}
        </button>
      </form>

      {itinerary && (
        <div className="itinerary markdown-output">
          <h2>Your Itinerary</h2>
          <ReactMarkdown>{itinerary}</ReactMarkdown>
        </div>
      )}

      {itinerary && (
        <div className="chat-container">
          <h2>Chat with AI</h2>
          <div className="chat-box">
            {chatMessages.map((msg, index) => (
              <div key={index} className={msg.sender === "You" ? "user-msg" : "ai-msg"}>
                <strong>{msg.sender}:</strong> {msg.text}
              </div>
            ))}
          </div>
          <form onSubmit={handleChatSubmit} className="chat-form">
            <input
              type="text"
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              placeholder="Ask follow-up questions..."
            />
            <button type="submit">Send</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default App;
