import { useState } from "react";
import { Button, Input, Textarea, Select } from "./components/ui/index";
import { Card, CardContent } from "./components/ui/card/index";


export default function TravelPlanner() {
  const [budget, setBudget] = useState("Moderate");
  const [duration, setDuration] = useState(3);
  const [destination, setDestination] = useState("");
  const [purpose, setPurpose] = useState("Leisure");
  const [preferences, setPreferences] = useState("");
  const [itinerary, setItinerary] = useState(null);

  const generateItinerary = async () => {
    if (!destination) {
      alert("Please enter a destination.");
      return;
    }
    const response = await fetch("http://localhost:5000/generate", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ budget, duration, destination, purpose, preferences })
    });
    const data = await response.json();
    setItinerary(data.itinerary);
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">AI Travel Planner</h1>
      <Card>
        <CardContent className="space-y-4">
          <Select value={budget} onChange={(e) => setBudget(e.target.value)}>
            <option>Low</option>
            <option>Moderate</option>
            <option>Luxury</option>
          </Select>
          <Input type="number" value={duration} onChange={(e) => setDuration(e.target.value)} min="1" placeholder="Duration (days)" />
          <Input type="text" value={destination} onChange={(e) => setDestination(e.target.value)} placeholder="Destination" />
          <Select value={purpose} onChange={(e) => setPurpose(e.target.value)}>
            <option>Leisure</option>
            <option>Adventure</option>
            <option>Culture</option>
            <option>Business</option>
          </Select>
          <Textarea value={preferences} onChange={(e) => setPreferences(e.target.value)} placeholder="Enter any preferences (e.g., food, activities)" />
          <Button onClick={generateItinerary} className="w-full">Generate Itinerary</Button>
        </CardContent>
      </Card>
      {itinerary && (
        <div className="mt-6 p-4 border rounded-lg">
          <h2 className="text-lg font-semibold">Your Itinerary:</h2>
          <p>{itinerary}</p>
        </div>
      )}
    </div>
  );
}
