import React, { useState, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import "./index.css";

const App = () => {
  const [formData, setFormData] = useState({
    budget: "Affordable",
    customBudget: "",
    currency: "USD",
    duration: "",
    address: "",
    destination: "",
    purpose: "Leisure",
    customPurpose: "",
    preferences: "",
  });
  const [itinerary, setItinerary] = useState("");
  const [loading, setLoading] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [userInput, setUserInput] = useState("");
  const [abortController, setAbortController] = useState(null);

  useEffect(() => {
    if (itinerary) {
      // Timeout to ensure the DOM has updated before scrolling
      setTimeout(() => {
        window.scrollTo({ top: document.body.scrollHeight, behavior: "smooth" });
      }, 100);
    }
  }, [itinerary]);



  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setItinerary("");
    const controller = new AbortController();
    setAbortController(controller);

    try {
      const budgetValue =
        formData.budget === "Custom"
          ? `${formData.customBudget} ${formData.currency}`
          : formData.budget;
      const purposeValue =
        formData.purpose === "Other" ? formData.customPurpose : formData.purpose;

      const requestBody = {
        budget: budgetValue,
        duration: formData.duration,
        destination: formData.destination,
        purpose: purposeValue,
        preferences: formData.preferences,
        ...(formData.address && { address: formData.address }),
      };

      const response = await fetch("http://localhost:5000/generate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(requestBody),
        signal: controller.signal, 
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      if (!response.body) {
        throw new Error("ReadableStream not supported in this browser.");
      }

      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let result = "";

      while (true) {
        const { value, done } = await reader.read();
        if (done) break;
        result += decoder.decode(value, { stream: true });
        setItinerary(result); // Update itinerary text as it streams in.
      }
    } catch (error) {
      console.error("Fetch error:", error);
      // Only show error if itinerary is empty (no partial data received yet)
      if (!itinerary) {
        setItinerary("Failed to connect to server. Please try again later.");
      }
      // If aborted, we don't want to show an error message
      if (error.name !== 'AbortError') {
        // Only update the itinerary with error if it wasn't an abort
        if (!itinerary) {
          setItinerary("Failed to connect to server. Please try again later.");
        }
      }
    }

    setLoading(false);
    setAbortController(null);
  };

  const handleStop = () => {
    if (abortController) {
      abortController.abort(); // Stop the fetch request
      setAbortController(null);
      setLoading(false);
      // Don't clear the itinerary - keep what's already loaded
    }
  };


  return (
    <div className="container">

      <h1>Travel Itinerary Generator</h1>
      <form onSubmit={handleSubmit}>
        <label>Budget:</label>
        <select name="budget" value={formData.budget} onChange={handleChange}>
          <option value="Luxury">Luxury</option>
          <option value="Premium">Premium</option>
          <option value="Affordable">Affordable</option>
          <option value="Backpacker">Backpacker</option>
          <option value="Custom">Custom</option>
        </select>
        {formData.budget === "Custom" && (
          <div className="inline-fields">
            <input
              type="number"
              name="customBudget"
              value={formData.customBudget}
              onChange={handleChange}
              required
              placeholder="e.g., 500"
            />
            <select name="currency" value={formData.currency} onChange={handleChange}>
              <option value="USD">USD</option>
              <option value="EUR">EUR</option>
              <option value="INR">INR</option>
              <option value="GBP">GBP</option>
            </select>
          </div>
        )}

        <label>Duration (in days):</label>
        <input
          type="number"
          name="duration"
          value={formData.duration}
          onChange={handleChange}
          required
          placeholder="e.g., 7"
        />

        <label>Destination:</label>
        <input
          type="text"
          name="destination"
          value={formData.destination}
          onChange={handleChange}
          required
          placeholder="e.g., Paris, France"
        />

        <label>Address (optional):</label>
        <input
          type="text"
          name="address"
          value={formData.address}
          onChange={handleChange}
          placeholder="e.g., 221B Baker Street, London"
        />

        <label>Purpose:</label>
        <select name="purpose" value={formData.purpose} onChange={handleChange}>
          <option value="Leisure">Leisure</option>
          <option value="Business">Business</option>
          <option value="Adventure">Adventure</option>
          <option value="Cultural">Cultural</option>
          <option value="Other">Other</option>
        </select>
        {formData.purpose === "Other" && (
          <input
            type="text"
            name="customPurpose"
            value={formData.customPurpose}
            onChange={handleChange}
            required
            placeholder="e.g., Family Reunion"
          />
        )}

        <label>Preferences (optional):</label>
        <input
          type="text"
          name="preferences"
          value={formData.preferences}
          onChange={handleChange}
          placeholder="e.g., Vegetarian food, Hiking trails"
        />
        <button type="submit" className="submit-btn" disabled={loading}>
          {loading ? "Generating..." : "Generate Itinerary"}
        </button>
        { loading &&(
          <div className="button-container">
            <button type="button" className="stop-btn" onClick={handleStop}>
              Stop
            </button>
          </div>
        )}
      </form>

      {itinerary && (
        <div className="itinerary markdown-output">
          <h2>Your Itinerary</h2>
          <ReactMarkdown>{itinerary}</ReactMarkdown>
        </div>
      )}

      {itinerary && (
        <div className="chat-container">
          <h2>Chat with AI</h2>
          <div className="chat-box">
            {chatMessages.map((msg, index) => (
              <div key={index} className={msg.sender === "You" ? "user-msg" : "ai-msg"}>
                <strong>{msg.sender}:</strong> {msg.text}
              </div>
            ))}
          </div>
          <form onSubmit={handleSubmit} className="chat-form">
            <input
              type="text"
              value={userInput}
              onChange={(e) => setUserInput(e.target.value)}
              placeholder="Ask follow-up questions..."
            />
            <button type="submit">Send</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default App;
