import React, { useState, useEffect, useRef } from "react";
import ReactMarkdown from "react-markdown";
import "./index.css";

const App = () => {
  const [formData, setFormData] = useState({
    budget: "Affordable",
    customBudget: "",
    currency: "USD",
    duration: "",
    address: "",
    destination: "",
    purpose: "Leisure",
    customPurpose: "",
    preferences: "",
  });

  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const budgetValue =
        formData.budget === "Custom"
          ? `${formData.customBudget} ${formData.currency}`
          : formData.budget;
      const purposeValue =
        formData.purpose === "Other" ? formData.customPurpose : formData.purpose;

      const response = await fetch("http://localhost:5000/generate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          budget: budgetValue,
          duration: formData.duration,
          address: formData.address,
          destination: formData.destination,
          purpose: purposeValue,
          preferences: formData.preferences,
        }),
      });
      const data = await response.json();

      setMessages([{
        type: "bot",
        content: data.itinerary || "Error generating itinerary."
      }]);
    } catch (error) {
      setMessages([{ type: "bot", content: "Failed to connect to server." }]);
    }
    setLoading(false);
  };

  const handleUserReply = async (userMessage) => {
    setMessages(prev => [...prev, { type: "user", content: userMessage }]);
    setLoading(true);

    try {
      const response = await fetch("http://localhost:5000/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ user_input: userMessage })
      });
      const data = await response.json();

      setMessages(prev => [...prev, { type: "bot", content: data.follow_up || "I couldn't process that." }]);
    } catch (error) {
      setMessages(prev => [...prev, { type: "bot", content: "Error fetching response." }]);
    }
    setLoading(false);
  };

  return (
    <div className="container">
      {loading && <div className="overlay"><div className="spinner"></div></div>}
      <h1>Travel Itinerary Generator</h1>

      {messages.length === 0 ? (
        <form onSubmit={handleSubmit}>
          <label>Budget:</label>
          <select name="budget" value={formData.budget} onChange={handleChange}>
            <option value="Luxury">Luxury</option>
            <option value="Premium">Premium</option>
            <option value="Affordable">Affordable</option>
            <option value="Backpacker">Backpacker</option>
            <option value="Custom">Custom</option>
          </select>
          {formData.budget === "Custom" && (
            <div className="inline-fields">
              <input type="number" name="customBudget" value={formData.customBudget} onChange={handleChange} required placeholder="e.g., 500" />
              <select name="currency" value={formData.currency} onChange={handleChange}>
                <option value="USD">USD</option>
                <option value="EUR">EUR</option>
                <option value="INR">INR</option>
                <option value="GBP">GBP</option>
              </select>
            </div>
          )}
          <label>Destination:</label>
          <input type="text" name="destination" value={formData.destination} onChange={handleChange} required placeholder="e.g., Paris, France" />
          <button type="submit" className="submit-btn" disabled={loading}>{loading ? "Generating..." : "Generate Itinerary"}</button>
        </form>
      ) : (
        <div className="chat-container">
          {messages.map((msg, index) => (
            <div key={index} className={`chat-message ${msg.type}`}>
              <ReactMarkdown>{msg.content}</ReactMarkdown>
            </div>
          ))}
          <div ref={chatEndRef} />
          <input
            type="text"
            placeholder="Your reply..."
            className="chat-input"
            onKeyDown={(e) => e.key === "Enter" && handleUserReply(e.target.value)}
          />
        </div>
      )}
    </div>
  );
};

export default App;
